assert_once(Fact) :- (Fact, !; assertz(Fact)).

get_pairs(List, Pairs) :-
	findall((X, Y), (member(X, List), member(Y, List), X \= Y), Pairs).

assert_list([]) :- !.
assert_list(List) :-
	append([(X, Y)], Rest, List),
	assert_once(word2vec(X, Y)),
	assert_list(Rest).

assert_all([]) :- !.
assert_all(List) :-
	append([Inner_List], Rest, List),
	get_pairs(Inner_List, Pairs),
	assert_list(Pairs),
	assert_all(Rest).

word2vec_2_aleph :-
	clusters(List),
	assert_all(List),
	tell('word2vec_temp.pl'), 
	listing(word2vec/2),
	told.
